usageExample:
const reaction = createReactionAction(() => ({ event: myEvent }), ndk);

// Get all reactions sorted by count
reaction.all  // EmojiReaction[] - { emoji, count, hasReacted, pubkeys, ... }

// Get total reaction count
reaction.totalCount  // number

// Get data for specific emoji
reaction.get("+")  // EmojiReaction | undefined

// React with standard emoji
await reaction.react("+");

// React with custom emoji (NIP-30)
await reaction.react({ emoji: ":custom:", shortcode: "custom", url: "https://..." });

// With delayed/cancellable reactions (5 seconds)
const reaction = createReactionAction(() => ({ event: myEvent, delayed: 5 }), ndk);
