usageExample:
const replyAction = createReplyAction(() => ({ event: myEvent }), ndk);

// Get reply count
replyAction.count  // number

// Check if current user replied
replyAction.hasReplied  // boolean

// Get all pubkeys who replied
replyAction.pubkeys  // string[]

// Create a reply
const replyEvent = await replyAction.reply("Great post!");
