usageExample:
const repostAction = createRepostAction(() => ({ event: myEvent }), ndk);

// Get repost count (unique users)
repostAction.count  // number

// Check if current user reposted
repostAction.hasReposted  // boolean

// Get all pubkeys who reposted
repostAction.pubkeys  // string[]

// Toggle repost
await repostAction.repost();

// Create a quote post with commentary
await repostAction.quote("This is amazing!");
